import {Link} from 'react-router-dom'
import { useState, useCallback, useEffect} from 'react'
import Book from './Book'
import * as BooksAPI from '../BooksAPI'
import {debounce} from 'lodash'

const Search = ({onUpdateShelf, getShelfByID}) => {

    

   
    const [searchQuery, setSearchQuery] = useState("");
    
    const [booksToShow, setBooksToShow] = useState([]);

    const updateSearchQuery = (event) => {
        setSearchQuery(event.target.value);
        // searchBooksLibrary(event.target.value);
    };

    // const searchBooksLibrary = useCallback(
    //   debounce(async (searchQuery) => { 

    //       const response = await BooksAPI.search(searchQuery, 20);
    //       const result = response && response.length? response : [];
    //       setBooksToShow(result);
    //   } , 500)
    // , [] )

    useEffect(() => {

        let isMounted = true;
        console.log(isMounted)
        const searchForBooks = async () => {
            const response = await BooksAPI.search(searchQuery);
            !response || response.error ? console.log(response) : isMounted && setBooksToShow(response)
        };
        
        searchQuery && searchForBooks();
        return (isMounted = false);
    }

    , [searchQuery]);
 
    return (
        <div className="search-books">
            <div className="search-books-bar">
                <Link
                to="/"
                className="close-search"
                >
                Close
                </Link>
                <div className="search-books-input-wrapper">
                    <input
                        type="text"
                        placeholder="Search by title, author, or ISBN"
                        value={searchQuery}
                        onChange={updateSearchQuery}
                    />
                </div>
            </div>
            <div className="search-books-results">
                <ol className="books-grid">
                    {
                        console.log(booksToShow)
                    }
                    {
                        booksToShow &&
                        booksToShow.length ? 
                            booksToShow.map((book) => {
                                return ( <li key={book.id}> <Book book={book} onUpdateShelf={onUpdateShelf} shelfName={getShelfByID(book.id)}/> </li> ) 
                            }) :
                            <h4>No results to show!</h4>
                    }
                </ol>
                
            </div>
        </div>
    );
};

export default Search;
