import {Link} from 'react-router-dom'
import { useState } from 'react'
import Book from './Book'

const Search = ({onUpdateShelf, onSearchRequest, getBookShelfByID}) => {

    const getBookShelf = async (book) => {
        const bookShelf = await getBookShelfByID(book.id);
        return bookShelf;
    }

   
    const [searchQuery, setSearchQuery] = useState("");
    
    const [booksToShow, setBooksToShow] = useState([]);

    const updateSearchQuery = (event) => {
        setSearchQuery(event.target.value);
        
        const searchBooksLibraryTimeout = setTimeout(() => {

            const updateSearchResults = async () => {

                const result = event.target.value === "" ? [] : await onSearchRequest(event.target.value, 40);
                setBooksToShow(result);

                // console.log("request made")
                // console.log(result)
            };

            clearTimeout(searchBooksLibraryTimeout);
            updateSearchResults();
        }, 500);

    };

    const mapBooks = async (booksToMap) => {
        const booksWithUnupdatedShelves = 
            booksToMap.map(async (book) => <li key={book.id}> <Book book={book} onUpdateShelf={onUpdateShelf} shelfName={await getBookShelf(book)}/> </li>)

        const booksWithUpdatedShelves = await Promise.all(booksWithUnupdatedShelves);
        return booksWithUpdatedShelves
    }
        

    return (
        <div className="search-books">
            <div className="search-books-bar">
                <Link
                to="/"
                className="close-search"
                >
                Close
                </Link>
                <div className="search-books-input-wrapper">
                    <input
                        type="text"
                        placeholder="Search by title, author, or ISBN"
                        value={searchQuery}
                        onChange={updateSearchQuery}
                    />
                </div>
            </div>
            <div className="search-books-results">
                <ol className="books-grid">
                    {
                        booksToShow.length ? 
                            mapBooks(booksToShow) :
                            <h4>Results will show here!</h4>
                    }
                </ol>
                
            </div>
        </div>
    );
};

export default Search;
