import "./App.css";
import { useState, useEffect, useCallback } from "react";
import { Routes, Route} from 'react-router-dom'
import Shelves from "./components/Shelves";
import Search from "./components/Search";
import * as BooksAPI from "./BooksAPI"
import { debounce } from 'throttle-debounce'

function App() {
  
  const [allBooks, setAllBooks] = useState([]);

  const updateShelf = async (book, shelf) => {
    await BooksAPI.update(book,shelf);
    getBooks();
  };



  const searchBooksLibrary = useCallback(debounce((searchQuery) => {
    async (searchQuery) => {
      const response = await BooksAPI.search(searchQuery, 40);
      return response.error ? [] : response;
    }
  }, 1000))

  const getBookShelfByID = (id) => {
    const targetBook = allBooks.filter((book) => book.id === id)
    return targetBook.length === 1 ? targetBook[0].shelf : "none"
  }
  const getBooks = async () => {
    const response = await BooksAPI.getAll();
    setAllBooks(response);
    
    console.log(response);
  };

  useEffect(() => {

    getBooks();

  }, []);


  return (
    <div className="app">

      <Routes>
        <Route exact path="/" element = {<Shelves books={allBooks} onUpdateShelf={updateShelf} />}/>
        <Route path="/search" element = {<Search onUpdateShelf={updateShelf} onSearchRequest={searchBooksLibrary} getShelfByID={getBookShelfByID}/>}/>
      </Routes>
      
    </div>
  );
};

export default App;
