import { FaStar } from 'react-icons/fa'

const StarsRating = ({rating}) => {
    
    return (
        <div className='show-book-rating'>
            {[...Array(5)].map((star, i) => {
                const ratingVal = i+1

                return <FaStar size= {40} className='star' value = {ratingVal} color={ratingVal <= rating ? '#ffc107' : '#e4e5e9'} />
            })}
        </div>
    )
}

// yellow: #ffc107      gray: #e4e5e9
export default StarsRating
