const Book = ({book, onUpdateShelf}) => {

    const selectShelf = (event) => {
        onUpdateShelf(book, event.target.value)
    }

    return (
        <div className="book">

            <div className="book-top">
                <div
                    className="book-cover"
                    style={{
                    width: 128,
                    height: 192,
                    backgroundImage: thumbnail && `url(${book.imageLinks.thumbnail})` ,
                    }}
                ></div>

                <div className="book-shelf-changer">
                    <select onChange={selectShelf}>
                        <option value="none" disabled>Move to...</option>
                        <option value="currentlyReading">Currently Reading</option>
                        <option value="wantToRead">Want to Read</option>
                        <option value="read">Read</option>
                        <option value="none">None</option>

                        {/* TODO: On click event call update Shelf */}
                    </select>
                </div>
            </div>

            <div className="book-title">{book.title}</div>
            {console.log(`Authors of ${book.title} are:   ${book.authors}`)}
            <div className="book-authors">{ book.authors ? book.authors.map( (author) => <div>{author}</div> ) : <span>no authors</span> }</div>
        </div>
    )
}

export default Book
