import { useParams, Link } from 'react-router-dom'
import { useState , useEffect } from 'react'

const BookPage = ({getBook}) => {

    const undefinedThumbnail = process.env.PUBLIC_URL + '/undefined thumbnail.png';

    const [book, setBook] = useState({})
    const params = useParams().bookId

    const getBookToShow = async () => {
        
        const res = await getBook(params)
        setBook(res)
        console.log(book)
    }

    useEffect(() => {
        getBookToShow()
    }, [])

    return(
        <div>
            <Link
                to="/"
                className="close-search"
                >
                Close
                </Link>
               

                {/* <div
                    className="book-cover"
                    style={{
                    width: 256,
                    height: 384,
                    backgroundImage: ,
                    }}
                ></div> */}
{/* {book.imageLinks && book.imageLinks.thumbnail ? `url(${book.imageLinks.thumbnail})` : `url(${undefinedThumbnail})`}
                    */}
                
                <img src={book && book.imageLinks && book.imageLinks.thumbnail? `url(${book.imageLinks.thumbnail})` : `url(${undefinedThumbnail})`}

                    className='book-cover' 
                    style={{
                        width: 256,
                        height: 384,
                
                    }}
                >
                    {
                        console.log(book)
                    }
                </img>

        </div>
    )
}

export default BookPage
