import {Link} from 'react-router-dom'
import {  useEffect, useState } from 'react'
import Book from './Book'

const Search = ({onUpdateShelf, onSearchRequest}) => {

    
    const [searchQuery, setSearchQuery] = useState("");
    
    const [booksToShow, setBooksToShow] = useState([]);

    const updateSearchQuery = async (event) => {
        setSearchQuery(event.target.value);
        // const result = event.target.value === "" ? [] : await onSearchRequest(event.target.value, 60);
        // setBooksToShow(result);
    };

    useEffect(() => {

        const searchLibTimeout = setTimeout(() => {
            const updateSearchResults = async () => {
                const result = searchQuery === "" ? [] : await onSearchRequest(searchQuery, 60);
                setBooksToShow(result);
            }
            
            updateSearchResults();
        }, 2000);
        
        

        return (
            clearTimeout(searchLibTimeout)
        )
    }, [searchQuery]);

    return (
        <div className="search-books">
            <div className="search-books-bar">
                <Link
                to="/"
                className="close-search"
                >
                Close
                </Link>
                <div className="search-books-input-wrapper">
                    <input
                        type="text"
                        placeholder="Search by title, author, or ISBN"
                        value={searchQuery}
                        onChange={updateSearchQuery}
                    />
                </div>
            </div>
            <div className="search-books-results">
                <ol className="books-grid">
                    {
                        booksToShow.length ? 
                            booksToShow.map((book) => <li key={book.id}> <Book book={book} onUpdateShelf={onUpdateShelf}/> </li>) :
                            <h4>Results will show here!</h4>
                    }
                </ol>
                
            </div>
        </div>
    );
};

export default Search;
