import { Dropdown, DropdownButton } from 'react-bootstrap';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import 'bootstrap/dist/css/bootstrap.min.css';


const Book = ({book, onUpdateShelf, shelfName}) => {

    let selectedOption = shelfName;

    // console.log(`${book.title}    SHELF: ${shelfName}`)
    const selectShelf = (event) => {
        onUpdateShelf(book, event.target.value);
        selectedOption = event.target.value;
    };

    const undefinedThumbnail = process.env.PUBLIC_URL + '/undefined thumbnail.png';

    return (
        <div className="book">
            <div className="book-top">
                <div
                    className="book-cover"
                    style={{
                    width: 128,
                    height: 192,
                    backgroundImage: book.imageLinks && book.imageLinks.thumbnail ? `url(${book.imageLinks.thumbnail})`
                    :
                    `url(${undefinedThumbnail})`,
                    }}
                ></div>

                <div className="book-shelf-changer">
                    {/* <select onChange={selectShelf} value={selectedOption}>
                        <option value="default" disabled>Move to...</option>
                        <option value="currentlyReading">Currently Reading</option>
                        <option value="wantToRead">Want to Read</option>
                        <option value="read">Read</option>
                        <option value="none">None</option>

                    </select> */}

                    <DropdownButton variant ="success" as={ButtonGroup} >
                        <Dropdown.Item eventKey="1">Currently Reading</Dropdown.Item>
                        <Dropdown.Item eventKey="2">Want to Read</Dropdown.Item>
                        <Dropdown.Item eventKey="3">Read</Dropdown.Item>
                        <Dropdown.Divider/>
                        <Dropdown.Item eventKey="4" active>None</Dropdown.Item>
                    </DropdownButton>
                </div>
            </div>

            <div className="book-title">{book.title}</div>
            <div className="book-authors">{ book.authors ? book.authors.map( (author) => <div key={author}>{author}</div> ) : <span></span>}</div>
        </div>
    )
}

export default Book
