import { useParams, Link } from 'react-router-dom'
import { useState , useEffect } from 'react'

const BookPage = ({getBook}) => {

    const undefinedThumbnail = process.env.PUBLIC_URL + '/undefined thumbnail.png';
    const starsRating = process.env.PUBLIC_URL + '/5 Stars Rating.png';

    const [book, setBook] = useState({})
    const params = useParams().bookId

    const getBookToShow = async () => {
        
        const res = await getBook(params)
        setBook(res)
        console.log(res)
    }

    useEffect(() => {
        getBookToShow()
    }, [])

    return(
        <div className='show-book-page'>
            <Link
                to="/"
                className="close-search"
                >
                Close
                </Link>
                
                <div className='show-book-container'>
                    <img src={book && book.imageLinks && book.imageLinks.thumbnail?  book.imageLinks.thumbnail : undefinedThumbnail}
                        className='show-book-cover' 
                    />

                    <div className='show-book-rating'>
                        <div style = {{width: `calc(20% * ${book.averageRating})` }}>
                            {book.averageRating ? book.averageRating : 'No Rating'}
                        </div>
                        <img src= {starsRating} />
                    </div>
                </div>


                

        </div>
    )
}

export default BookPage
